const paragraph = document.getElementById('paragraph');
const text = paragraph.split('');
var index = 0;

function Typewrite(){
    if(index == 0) {
        paragraph.innerHTML = ''
        paragraph.innerHTML = text[index]
    }if(index <= text.length()){
        paragraph.innerHTML += text[index]
        index++
        setTimeout(Typewrite, 30)
    }
}

export default Typewrite
